def meal_plan(wake_up, sleep_time, diet_type, region, calories_needed):
    convo.send_message(
        f"wake up time : {wake_up} sleep time : {sleep_time}, dietType: {diet_type}, region:{region}, caloriesintake:{calories_needed} dont add value of g in macro create a single dictionary with main key as 'mealplan' withing this main key create nested dictionary for mealtype like breakfast,lunchetc and dont combing mealtype and time create seperate key ")
    data = convo.last.text
    meal = data.replace("json", "")
    meal = meal.replace("```", "")  # Remove triple quotes
    meal_dict = eval(meal)
    return meal_dict['mealplan']
